mapper.insert:

INSERT INTO TB_COM_USER(
		USER_CODE
	  , ID
	  , PW
	  , EMAIL
	  , USER_IMAGE
	  , NAME
	  , BIRTH
	  , POST_NUM
	  , PHONE_CD
	  , PHONE_NUM
	  , ADDRESS
	  , ENT_DATE
	  ) VALUES(
	  'USER' || LPAD(USER_SEQ.NEXTVAL, 4, 0)
	  , #{id}
	  , #{pw}
	  , #{email}
	  , #{userImage}
	  , #{name}
	  , #{birth}
	  , #{postNum}
	  , #{phoneCd}
	  , #{phoneNum}
	  , #{address}
	  , TO_CHAR(SYSDATE, 'YYYY-MM-DD-HH24:MI')
	  )

mapper.update:

	UPDATE TB_COM_USER
	SET	EMAIL       = #{email}
	  , USER_IMAGE  = #{userImage}
	  , NAME        = #{name}
	  , BIRTH       = #{birth}
	  , POST_NUM    = #{postNum}
	  , PHONE_CD    = #{phoneCd}
      , PHONE_NUM   = #{phoneNum}
	  , ADDRESS     = #{address}
	WHERE USER_CODE = #{userCode}
